"use strict";function Backtop(){this.oBacktop=document.querySelector(".back-top"),this.init()}Backtop.prototype={init:function(){this.clickoBacktop(),this.overoBacktop(),this.outoBacktop(),this.scrollwindow()},clickoBacktop:function(){this.oBacktop.addEventListener("click",this.clickoBacktopCb.bind(this))},clickoBacktopCb:function(){document.documentElement.scrollTop?document.documentElement.scrollTop=0:document.body.scrollTop=0},overoBacktop:function(){this.oBacktop.addEventListener("mouseover",this.overoBacktopCb.bind(this))},overoBacktopCb:function(){this.oBacktop.style.background="url(imgs/top.png) no-repeat -100px center #5d5f5e",this.oBacktop.innerText="返回顶部"},outoBacktop:function(){this.oBacktop.addEventListener("mouseout",this.outoBacktopCb.bind(this))},outoBacktopCb:function(){this.oBacktop.style.background="url(imgs/top.png) no-repeat -42px center #5d5f5e",this.oBacktop.innerText=""},scrollwindow:function(){window.addEventListener("scroll",this.scrollwindowCb.bind(this))},scrollwindowCb:function(){var o=document.documentElement.scrollTop||document.body.scrollTop;this.oBacktop.style.display=100<=o?"block":"none"}},new Backtop;